var editorWrapper=document.getElementById("editor"),previewWrapper=document.getElementById("preview"),characterCount=document.getElementById("characterCount"),init=function(){loadData(),editor(editorWrapper,previewWrapper)},loadData=function(){editorWrapper.innerHTML=localStorage.getItem("markdown")},editor=function(e,t){this.update=function(){t.innerHTML=markdown.toHTML(e.value)},this.save=function(){localStorage.setItem("markdown",e.value)},this.characters=function(){var e=localStorage.getItem("markdown").length;document.getElementById("characterCount").innerHTML=e},e.editor=this,this.update(),this.save(),this.characters()};init();